function enter()
{
	extract ($_REQUEST);

	$fields = array();
	$fields["project_id"] = 0;
	$fields["version"] = "Version 0.1";

	extract ($fields, EXTR_SKIP);

	$sql = "SELECT * FROM project.projects";
	$proj_rslt = db_exec($sql) or errDie("Unable to retrieve projects.");

	$project_sel = "<select name='project_id' style='width: 100%'>";
	while ($proj_data = pg_fetch_array($proj_rslt)) {
		if ($project_id == $proj_data["id"]) {
			$sel = "selected";
		} else {
			$sel = "";
		}

		$project_sel .= "<option value='$proj_data[id]' $sel>
			$proj_data[name]
		</option>";
	}
	$project_sel .= "</select>";

	if ($project_id) {
		$sql = "SELECT * FROM project.projects WHERE id='$project_id'";
		$proj_rslt = db_exec($sql) or errDie("Unable to retrieve project.");
		$proj_data = pg_fetch_array($proj_rslt);

		$project_name = $proj_data["name"];
	} else {
		$project_name = "Project Name";
	}

	$showdoc_html = "<style> <!-- @page { size: 8.27in 11.69in; margin: 0.79in } H4 { margin-top: 0in; margin-bottom: 0in; text-align: center } H4.western { font-family: \"Arial\", sans-serif; font-size: 22pt; font-weight: medium } H4.cjk { font-family: \"Bitstream Vera Sans\"; font-size: 22pt; font-weight: medium } H4.ctl { font-family: \"Tahoma\", \"Lucidasans\", \"Lucida Sans\", \"Arial Unicode MS\"; font-size: 12pt; font-weight: medium } H5 { margin-top: 0in; margin-bottom: 0in; text-align: center } H5.western { font-family: \"Arial\", sans-serif; font-size: 16pt } H5.cjk { font-family: \"Bitstream Vera Sans\"; font-size: 16pt } H5.ctl { font-family: \"Tahoma\", \"Lucidasans\", \"Lucida Sans\", \"Arial Unicode MS\"; font-size: 12pt } H1 { margin-bottom: 0.04in } H1.western { font-family: \"Arial\", sans-serif; font-size: 16pt } H1.cjk { font-family: \"Bitstream Vera Sans\"; font-size: 16pt } H1.ctl { font-family: \"Arial\", sans-serif; font-size: 16pt } H3 { margin-bottom: 0.04in } H3.western { font-family: \"Arial\", sans-serif; font-size: 13pt } H3.cjk { font-family: \"Bitstream Vera Sans\"; font-size: 13pt } H3.ctl { font-family: \"Arial\", sans-serif; font-size: 13pt } H2 { margin-top: 0in; margin-bottom: 0in } H2.western { font-family: \"Arial\", sans-serif; font-size: 12pt } H2.cjk { font-family: \"Bitstream Vera Sans\"; font-size: 12pt } H2.ctl { font-family: \"Tahoma\", \"Lucidasans\", \"Lucida Sans\", \"Arial Unicode MS\"; font-size: 12pt } P { margin-bottom: 0.08in } --> </style> <p style=\"margin-bottom: 0in;\"><br> </p> <p style=\"margin-bottom: 0in;\"><br> </p> <p style=\"margin-bottom: 0in;\" align=\"center\"><br> </p> <p style=\"margin-bottom: 0in;\" align=\"center\"><br> </p> <p style=\"margin-bottom: 0in;\" align=\"center\"><br> </p> <p style=\"margin-bottom: 0in;\" align=\"center\"><br> </p> <p style=\"margin-bottom: 0in;\"><br> </p> <p style=\"margin-bottom: 0in;\"><br> </p> <p style=\"margin-bottom: 0in;\"><br> </p> <p style=\"margin-bottom: 0in